plugin.phpcode:
if (!function_exists('fetch_all_usergroups'))
{
	function fetch_all_usergroups()
	{
		global $vbulletin;

		if (is_array($vbulletin->ugs))
		{
			return $vbulletin->ugs;
		}

		$ugs = array();

		$usergroups = $vbulletin->db->query('select usergroupid, title from ' . TABLE_PREFIX . 'usergroup order by `title`');
		while ($usergroup = $vbulletin->db->fetch_array($usergroups))
		{
			$ugs[$usergroup['usergroupid']] = $usergroup['title'];
		}

		$vbulletin->ugs = $ugs;

		return $vbulletin->ugs;
	}
}

if (in_array($setting['optioncode'], array('default---infernoshout---usergroups', 'default---infernoshout---usergroups---hidden', 'default---infernoshout---usergroups---protected', 'default---infernoshout---usergroups---bannedug', 'default---infernoshout---usergroups---archiveperm', 'default---infernoshout---usergroups---archivesearchperm')))
{
	$options = fetch_all_usergroups();

	$allowed = explode(',', $setting['value']);

	if (!is_array($allowed))
	{
		$allowed = array();
	}

	$setting['html'] = "<div id=\"ctrl_setting[$setting[varname]]\" class=\"smallfont\">\r\n";
	$setting['html'] .= "<input type=\"hidden\" name=\"setting[$setting[varname]][0]\" value=\"0\" />\r\n";

	foreach ($options AS $key => $value)
	{
		$value = str_replace('(COPPA) ', '', $value);
		$setting['html'] .= "<table style=\"width:200px; float:$stylevar[left]\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\"><tr valign=\"top\">
		<td><input type=\"checkbox\" name=\"setting[$setting[varname]][$key]\" id=\"setting[$setting[varname]]_$key\" value=\"1\"" . ((in_array($key, $allowed)) ? ' checked="checked"' : '') . " /></td>
		<td width=\"100%\" style=\"padding-top:4px\"><label title=\"$value\" for=\"setting[$setting[varname]]_$key\" class=\"smallfont\" style=\"white-space: nowrap;\">$value</label></td>\r\n</tr></table>\r\n";
	}

	print_label_row($description, $setting['html'], '', 'top', $name, 40);

	$handled = true;
}

