			<pre>./infernoshout/aop/aop.txt</pre>
			Просьба проверить, что даный файл имеет CHMOD-права 0666 (если это не работает, попробуйте 0777).
		');
	}
}

$exempt = explode(',', str_replace(' ', '', $this->registry->options['ishout_threadnotice']));
$stopnotice = false;

if (is_array($exempt))
{
	if (in_array($this->info['forum']['forumid'], $exempt) || $this->registry->options['ishout_postnoticedisable'])
	{
		$stopnotice = true;
	}
}

if (!$stopnotice && THIS_SCRIPT == 'newreply')
{
	global $infernoshout;

	require_once(DIR . '/infernoshout/engine/inferno_engine.php');
	$infernoshout->load_engine('shout');

	$titleprefix = '';
	$titlesuffix = '';

	if ($infernoshout->vbulletin->options['ishout_bbcodes'] & 64)
	{
		$titleprefix = '[post=' . intval($this->fetch_field('postid')) . ']';
		$titlesuffix = '[/post]';
	}

	$shout =& new shout;
	$shout->silent = true;
	$shout->process('/me ответил(а) в теме ' . $titleprefix . unhtmlspecialchars($this->info['thread']['title']) . $titlesuffix);
}

if ($this->registry->options['ishout_postmarker'] > 0)
{
	global $infernoshout;

	require_once(DIR . '/infernoshout/engine/inferno_engine.php');
	$infernoshout->load_engine('shout');

	$division = @(($this->registry->userinfo['posts'] + 1) / $this->registry->options['ishout_postmarker']);

	if ($division === intval($division))
	{
		$shout =& new shout;
		$shout->silent = true;
		$shout->process('/me достиг(ла) ' . ($this->registry->userinfo['posts'] + 1) . ' posts!');
	}
}

$exempt = explode(',', str_replace(' ', '', $this->registry->options['ishout_threadnotice']));
$stopnotice = false;

if (is_array($exempt))
{
	if (in_array($this->info['forum']['forumid'], $exempt) || $this->registry->options['ishout_postnoticedisable'])
	{
		$stopnotice = true;
	}
}

